      {
        title: 'Basic theming',
        icon: <Icon type="close-circle" theme="filled" />,
      },
      {
        title: 'Email Reciept',
        icon: <Icon type="close-circle" theme="filled" />,
      },
      {
        title: 'Expensive donor data',
        icon: <Icon type="close-circle" theme="filled" />,
      },
    ],
  },
  {
    title: 'Premium Plan',
    price: '175.00',
    type: 'monthly',
    features: [
      {
        title: 'Ultimate campaigns',
        icon: <Icon type="check-circle" theme="filled" />,
